import { Component } from 'react';
import logo from '../images/logo.svg';
import './App.css';

class App extends Component {
    constructor() {
      super();
      this.state = {
        name: {firstName: "Shaun", lastName: "Roecker"},
        company: "UPCR"
      };

    }

    nameStateChange = () => {
      this.setState(() => {
        return {
          name: {firstName: "Andrei", lastName: "Nagouie"}
        }
      }, () => {// <-- this is a callback, run after finishing first
        console.log(this.state.name)// function, because the first func is asynchronous
      }         
    )};

    render(){
      const { name, company } = this.state;
      return (
        <div className="App">
          <header className="App-header">

            <img src={logo} className="App-logo" alt="logo" />

            <h2>{`Hi I'm ${name.firstName} ${name.lastName}, 
                  I work at ${company}`}</h2>

            <button onClick={() => {this.setState(() => {
                return {
                  name: {firstName: "Andrei", lastName: "Nagouie"}
                }
              }, () => {
                console.log(name)
              }); // <-- this is a callback, run after finishing first
              }             // function, because the first func is asynchronous
            }>
              {`Change ${name.firstName}`}
            </button>
          </header>
        </div>
      );
    }
}
export default App;
