import { Component } from 'react';
import logo from '../images/logo.svg';
import './App.css';

class App extends Component {
    constructor() {
      super();
      this.state = {
        monster1: {
          name: "Linda"
        },
        monster2: {
          name: "Frank"
        },
        monster3: {
          name: "Jacky"
        }

      }
    }

    /*
    onNameChange = () => {
      this.setState(() => {
        return {
          name: {firstName: "Andrei", lastName: "Nagouie"}
        }
      }, () => {// <-- this is a callback, runs after finishing first function, 
        console.log(this.state.name)// because the first func is asynchronous
      }         
    )}; */

    render(){
      //const {  } = this.state;
      return (
        <div className="App">
          <h1>{this.state.monster1.name}</h1>
          <h1>{this.state.monster2.name}</h1>
          <h1>{this.state.monster3.name}</h1>
        </div>
      );
    }
}
export default App;
